(47, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'paypal-carding-recaptcha-jan-26', 'update', 'en_US', 'Fraud protection is now required — enable today', 'Card networks like Visa and Mastercard now require fraud prevention controls, and non-compliance may result in fines and processing restrictions. Please enable reCAPTCHA in your PayPal Payments settings to help protect your store and maintain compliance.', '{}', 'unactioned', 'woocommerce.com', '2026-02-16 11:53:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'Googleupdate_q1_2026', 'marketing', 'en_US', 'Make every ad dollar count', 'Google for WooCommerce now includes Budget Recommendations. Promote your products across Google — from Search to YouTube and beyond — and get personalized suggestions on where to optimize campaign budgets to reach more shoppers.', '{}', 'pending', 'woocommerce.com', '2026-02-16 11:51:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'ppcp-recaptcha-protection-note12', 'info', 'en_US', 'Fraud protection is now required — enable today', 'Card networks like Visa, Mastercard and American Express now require fraud prevention controls, and non-compliance may result in fines and processing restrictions. Please enable reCAPTCHA in your PayPal Payments settings to help protect your store and maintain compliance.', '{}', 'unactioned', 'woocommerce-paypal-payments', '2026-02-16 11:53:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-payments-notes-set-https-for-checkout', 'info', 'en_US', 'Włącz bezpieczną finalizację zamówienia.', 'Włącz protokół HTTPS na stronach finalizacji zamówień, aby wyświetlić wszystkie dostępne metody płatności i chronić dane swoich klientów.', '{}', 'unactioned', 'woocommerce-payments', '2026-02-16 11:53:24', NULL, 0, 'plain', '', 0, 0, 'info');
